// /client/src/App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Switch, Link } from 'react-router-dom';
import './App.css';
import axios from 'axios';

import Login from './components/Login';
import Register from './components/Register';
import ProductCard from './components/ProductCard';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [username, setUsername] = useState('');
  const [categories] = useState([
    'electronics',
    'jewelery',
    'men clothing',
    'women clothing',
  ]);
  const [selectedCategory, setSelectedCategory] = useState(categories[0]);
  const [searchTerm, setSearchTerm] = useState('');
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleCategoryChange = (e) => {
    setSelectedCategory(e.target.value);
    setProducts([]);
    setSearchTerm('');
    setError('');
  };

  const handleSearch = async () => {
    if (!selectedCategory || !searchTerm) return;

    setLoading(true);
    setError('');

    try {
      const response = await axios.get(
        `http://localhost:5000/api/products?category=${selectedCategory}&search=${searchTerm}`
      );
      setProducts(response.data);
    } catch (err) {
      console.error('Error fetching products:', err);
      setError('Failed to fetch products. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleLogin = (username) => {
    setUsername(username);
    setIsLoggedIn(true);
  };

  const handleRegister = (username) => {
    setUsername(username);
    setIsLoggedIn(true);
  };

  const Navbar = () => (
    <nav className="navbar">
      <div className="logo">ProCompare</div>
      <ul className="nav-links">
        <li><Link to="#home">Home</Link></li>
        <li><Link to="#about">About</Link></li>
        <li><Link to="#products">Products</Link></li>
        <li><Link to="#contact">Contact</Link></li>
      </ul>
    </nav>
  );

  const AboutSection = () => (
    <section id="about" className="section">
      <h2>About</h2>
      <p>Welcome to ProCompare! The ultimate platform for comparing products across different online stores.</p>
    </section>
  );

  const ContactSection = () => (
    <section id="contact" className="section">
      <h2>Contact</h2>
      <p>Feel free to reach out to us at contact@procompare.com</p>
    </section>
  );

  const ProductsSection = () => (
    <section id="products" className="product-list">
      {error && <p className="error-message">{error}</p>}
      {loading && <p>Loading products...</p>}
      {!loading && !error && products.length > 0 ? (
        products.map((productPair, index) => (
          <ProductCard key={index} productPair={productPair} />
        ))
      ) : (
        !loading && !error && <p>No products found. Try a different search term or category.</p>
      )}
    </section>
  );

  return (
    <Router>
      <div className="App">
        {isLoggedIn ? (
          <>
            <Navbar />
            <header>
              <h1>Find the Best Deals, Compare Products</h1>
              <div className="search-section">
                <select
                  value={selectedCategory}
                  onChange={handleCategoryChange}
                  className="category-selector"
                >
                  {categories.map((category, index) => (
                    <option key={index} value={category}>
                      {category.charAt(0).toUpperCase() + category.slice(1)}
                    </option>
                  ))}
                </select>
                <div className="search-bar">
                  <input
                    type="text"
                    placeholder="Search for products..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    disabled={!selectedCategory}
                  />
                  <button onClick={handleSearch} disabled={loading || !selectedCategory}>
                    {loading ? 'Loading...' : 'Search'}
                  </button>
                </div>
              </div>
            </header>
            <ProductsSection />
            <AboutSection />
            <ContactSection />
          </>
        ) : (
          <Switch>
            <Route path="/login">
              <Login onLogin={handleLogin} />
            </Route>
            <Route path="/register">
              <Register onRegister={handleRegister} />
            </Route>
            <Route path="/">
              <Login onLogin={handleLogin} />
            </Route>
          </Switch>
        )}
      </div>
    </Router>
  );
}

export default App;
