import React, { useState, useRef } from "react";
import { BrowserRouter as Router, Route, Routes, Link } from "react-router-dom";
import "./App.css";
import Login from "./components/Login";
import Register from "./components/Register";
import About from "./components/About";
import Contact from "./components/Contact";
import ProductCard from "./components/ProductCard";
import axios from "axios";
import Developers from "./components/Developers";
import ComparisonHistory from "./components/comparisonHistory";

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [categories] = useState(["electronics", "jewelry", "men_clothing", "women_clothing"]);
  const [selectedCategory, setSelectedCategory] = useState("");
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedPlatforms, setSelectedPlatforms] = useState([]);
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [username, setUsername] = useState("");

  const comparisonSectionRef = useRef(null); // Ref for scrolling to comparison section

  const handleLogin = (username) => {
    setUsername(username);
    setIsLoggedIn(true);
  };

  const handleRegister = (username) => {
    setUsername(username);
    alert(`Welcome, ${username}!`);
    setIsLoggedIn(true);
  };

  const handleCategoryChange = (e) => {
    setSelectedCategory(e.target.value);
    setProducts([]);
    setSearchTerm("");
    setError("");
  };

  const handleSearch = async () => {
    if (!selectedCategory || !searchTerm || selectedPlatforms.length === 0) {
      setError("Please select a category, enter a search term, and choose at least one platform.");
      return;
    }

    setLoading(true);
    setError("");

    try {
      const response = await axios.get(
        `http://localhost:5000/api/products?category=${selectedCategory}&search=${searchTerm}&platforms=${selectedPlatforms.join(
          ","
        )}&username=${username}`
      );
      setProducts(response.data);
    } catch (err) {
      console.error("Error fetching products:", err);
      setError("Failed to fetch products. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  const handlePlatformChange = (platform) => {
    setSelectedPlatforms((prev) =>
      prev.includes(platform) ? prev.filter((p) => p !== platform) : [...prev, platform]
    );
  };

  const scrollToComparison = () => {
    comparisonSectionRef.current.scrollIntoView({ behavior: "smooth" });
  };

  const Navbar = () => (
    <nav className="navbar">
      <div className="logo">ProCompare</div>
      <ul className="nav-links">
        <li><Link to="/">Home</Link></li>
        <li><Link to="/about">About</Link></li>
        <li><Link to="/contact">Contact</Link></li>
        <li><Link to="/developers">Developers</Link></li>
        <li><Link to="/profile">Profile</Link></li>
      </ul>
    </nav>
  );

  const ProductsSection = () => (
    <section id="products" ref={comparisonSectionRef}>
      {error && <p className="error-message">{error}</p>}
      {loading && <p>Loading products...</p>}
      {!loading && !error && products.length > 0 ? (
        <div className="product-cards">
          {products.map((productPair, index) => (
            <ProductCard key={index} productPair={productPair} />
          ))}
        </div>
      ) : (
        !loading && !error && <p>Search Products...</p>
      )}
    </section>
  );

  const HomePageIntro = () => (
    <div className="home-intro">
      <div className="intro-text">
        <h1>Welcome to ProCompare</h1>
        <button className="compare-button" onClick={scrollToComparison}>
          Compare
        </button>
      </div>
      <div className="intro-image">
        <img src="/path-to-your-image.jpg" alt="ProCompare" />
      </div>
    </div>
  );

  return (
    <Router>
      <div className="App">
        {isLoggedIn ? (
          <>
            <Navbar />
            <Routes>
              <Route
                path="/"
                element={
                  <>
                    <HomePageIntro />
                    <header>
                      <div className="search-section">
                        <select
                          value={selectedCategory}
                          onChange={handleCategoryChange}
                          className="category-selector"
                        >
                          <option value="" disabled>
                            Select Category
                          </option>
                          {categories.map((category, index) => (
                            <option key={index} value={category}>
                              {category.charAt(0).toUpperCase() + category.slice(1)}
                            </option>
                          ))}
                        </select>
                        <input
                          type="text"
                          placeholder="Search for products..."
                          value={searchTerm}
                          onChange={(e) => setSearchTerm(e.target.value)}
                          className="search-input"
                        />
                        <PlatformSelector />
                        <button
                          onClick={handleSearch}
                          disabled={loading || !selectedCategory}
                          className="search-button"
                        >
                          {loading ? "Loading..." : "Search"}
                        </button>
                      </div>
                    </header>
                    <ProductsSection />
                  </>
                }
              />
              <Route path="/about" element={<About />} />
              <Route path="/contact" element={<Contact />} />
              <Route path="/developers" element={<Developers />} />
              <Route path="/profile" element={<ComparisonHistory username={username} />} />
            </Routes>
          </>
        ) : (
          <Routes>
            <Route path="/" element={<Login onLogin={handleLogin} />} />
            <Route path="/register" element={<Register onRegister={handleRegister} />} />
          </Routes>
        )}
      </div>
    </Router>
  );
}

export default App;
