import React, { useState, useEffect, useRef } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import './App.css';
import Login from './components/Login';
import Register from './components/Register';
import About from './components/About';
import Contact from './components/Contact';
import ProductCard from './components/ProductCard';
import axios from 'axios';
import Developers from './components/Developers';
import ComparisonHistory from './components/comparisonHistory';

// Scroll animation hook
const useScrollAnimation = (ref) => {
  useEffect(() => {
    const handleScroll = () => {
      const element = ref.current;
      const position = element.getBoundingClientRect();

      // Check if the element is in the viewport
      if (position.top >= 0 && position.bottom <= window.innerHeight) {
        element.classList.add('visible');
      } else {
        element.classList.remove('visible');
      }
    };

    window.addEventListener('scroll', handleScroll);
    handleScroll(); // Call the function on initial render

    return () => window.removeEventListener('scroll', handleScroll);
  }, [ref]);

  return null;
};

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [categories] = useState([
    'electronics',
    'jewelry',
    'men_clothing',
    'women_clothing',
  ]);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedPlatforms, setSelectedPlatforms] = useState([]);
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [username, setUsername] = useState('');

  // Create refs for each benefit item
  const benefitRef1 = useRef(null);
  const benefitRef2 = useRef(null);
  const benefitRef3 = useRef(null);
  const benefitRef4 = useRef(null);

  // Call the useScrollAnimation hook for each ref
  useScrollAnimation(benefitRef1);
  useScrollAnimation(benefitRef2);
  useScrollAnimation(benefitRef3);
  useScrollAnimation(benefitRef4);

  const handleLogin = (username) => {
    setUsername(username);
    setIsLoggedIn(true);
  };

  const handleRegister = (username) => {
    setUsername(username);  // Set username after registration
    alert(`Welcome, ${username}!`);
    setIsLoggedIn(true); // Log the user in after successful registration
  };

  const handleCategoryChange = (e) => {
    setSelectedCategory(e.target.value);
    setProducts([]);
    setSearchTerm('');
    setError('');
  };

  const handleSearch = async () => {
    if (!selectedCategory || !searchTerm || selectedPlatforms.length === 0) {
      setError('Please select a category, enter a search term, and choose at least one platform.');
      return;
    }

    setLoading(true);
    setError('');

    try {
      const response = await axios.get(
        `http://localhost:5000/api/products?category=${selectedCategory}&search=${searchTerm}&platforms=${selectedPlatforms.join(',')}&username=${username}`
      );
      setProducts(response.data);
    } catch (err) {
      console.error('Error fetching products:', err);
      setError('Failed to fetch products. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handlePlatformChange = (platform) => {
    setSelectedPlatforms((prev) =>
      prev.includes(platform)
        ? prev.filter((p) => p !== platform)
        : [...prev, platform]
    );
  };

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen((prevState) => !prevState);
  };

  const Navbar = () => (
    <nav className={`navbar ${isMobileMenuOpen ? 'active' : ''}`}>
      <div className="logo">ProCompare</div>
      <ul className="nav-links">
        <li><Link to="/">Home</Link></li>
        <li><Link to="/about">About</Link></li>
        <li><Link to="/contact">Contact</Link></li>
        <li><Link to="/developers">Developers</Link></li>
        <li><Link to="/profile">Profile</Link></li>
      </ul>
      <div className="hamburger" onClick={toggleMobileMenu}>
        <div></div>
        <div></div>
        <div></div>
      </div>
    </nav>
  );

  const ProductsSection = () => (
    <section id="products" className="product-list">
      {error && <p className="error-message">{error}</p>}
      {loading && <p>Loading products...</p>}
      {!loading && !error && products.length > 0 ? (
        <div className="product-cards">
          {products.map((productPair, index) => (
            <ProductCard key={index} productPair={productPair} />
          ))}
        </div>
      ) : (
        !loading && !error && <p>Search Products...</p>
      )}
    </section>
  );

  const PlatformSelector = () => (
    <div className="platform-selector-container">
      <h3>Select Platforms:</h3>
      <div className="platform-selector">
        {['flipkart', 'amazon', 'myntra', 'meesho'].map((platform) => (
          <button
            key={platform}
            className={`platform-button ${selectedPlatforms.includes(platform) ? 'selected' : ''}`}
            onClick={() => handlePlatformChange(platform)}
          >
            {platform.charAt(0).toUpperCase() + platform.slice(1)}
          </button>
        ))}
      </div>
    </div>
  );

  const BenefitsSection = () => (
    <section className="benefits-section">
      <h2>Why Choose ProCompare ?</h2>
      <div className="benefits-container">
        <div ref={benefitRef1} className="benefit-item">
          <img src="i3.webp" alt="Comparison Feature 1" />
          <p>Compare products across multiple platforms effortlessly.</p>
        </div>
        <div ref={benefitRef2} className="benefit-item">
          <img src="i2.jpeg" alt="Comparison Feature 2" />
          <p>Save time and money by finding the best deals instantly.</p>
        </div>
        <div ref={benefitRef3} className="benefit-item">
          <img src="i4.jpeg" alt="Comparison Feature 3" />
          <p>Access detailed insights and product reviews.</p>
        </div>
        <div ref={benefitRef4} className="benefit-item">
          <img src="i1.jpg" alt="Comparison Feature 4" />
          <p>Track price trends and stay ahead of the market.</p>
        </div>
      </div>
    </section>
  );

  return (
    <Router>
      <div className="App">
        {isLoggedIn ? (
          <>
            <Navbar />
            <Routes>
              <Route
                path="/"
                element={
                  <>
                    <header id="home" className="home-layout">
                      <div className="left-side">
                        <h1>Welcome to ProCompare</h1>
                        <button className="compare-button">
                          Compare
                        </button>
                      </div>
                      <div className="right-side">
                        <img
                          src="finalmanleft.png"
                          alt="Home Illustration"
                          className="home-image"
                        />
                      </div>
                    </header>
                    <BenefitsSection />
                    <div className="search-section">
                      <div className="search-bar">
                        <select
                          value={selectedCategory}
                          onChange={handleCategoryChange}
                          className="category-selector"
                        >
                          <option value="" disabled>
                            Select Category
                          </option>
                          {categories.map((category, index) => (
                            <option key={index} value={category}>
                              {category.charAt(0).toUpperCase() + category.slice(1)}
                            </option>
                          ))}
                        </select>
                        <input
                          type="text"
                          placeholder="Search for products..."
                          value={searchTerm}
                          onChange={(e) => setSearchTerm(e.target.value)}
                          className="search-input"
                        />
                      </div>
                      <PlatformSelector />
                      <button
                        onClick={handleSearch}
                        disabled={loading || !selectedCategory}
                        className="search-button"
                      >
                        {loading ? 'Loading...' : 'Search'}
                      </button>
                    </div>
                    <ProductsSection />
                  </>
                }
              />
              <Route path="/about" element={<About />} />
              <Route path="/contact" element={<Contact />} />
              <Route path="/developers" element={<Developers />} />
              <Route path="/profile" element={<ComparisonHistory username={username} />} />
            </Routes>
          </>
        ) : (
          <Routes>
            <Route path="/" element={<Login onLogin={handleLogin} />} />
            <Route path="/register" element={<Register onRegister={handleRegister} />} />
          </Routes>
        )}
      </div>
    </Router>
  );
}

export default App;
