// /client/src/App.js
import React, { useState } from 'react';
import axios from 'axios';
import './App.css';
import ProductCard from './components/ProductCard';

function App() {
  const categories = ['electronics', 'jewelry']; // Add more categories if needed
  const [selectedCategory, setSelectedCategory] = useState(categories[0]);
  const [searchTerm, setSearchTerm] = useState('');
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleCategoryChange = (e) => {
    setSelectedCategory(e.target.value);
    setProducts([]);
    setSearchTerm('');
    setError('');
  };

  const handleSearch = async () => {
    if (!selectedCategory || !searchTerm) return;

    setLoading(true);
    setError('');

    try {
      const response = await axios.get(
        `http://localhost:5000/api/products?category=${selectedCategory}&search=${searchTerm}`
      );
      setProducts(response.data);
    } catch (err) {
      console.error('Error fetching products:', err);
      setError('Failed to fetch products. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <header>
        <h1>Product Comparison</h1>
        <div>
          <select value={selectedCategory} onChange={handleCategoryChange}>
            {categories.map((category, index) => (
              <option key={index} value={category}>
                {category.charAt(0).toUpperCase() + category.slice(1)}
              </option>
            ))}
          </select>
          <input
            type="text"
            placeholder="Search for products..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <button onClick={handleSearch} disabled={loading}>
            {loading ? 'Loading...' : 'Search'}
          </button>
        </div>
      </header>

      <main>
        {error && <p>{error}</p>}
        {loading && <p>Loading products...</p>}
        {!loading && products.length === 0 && <p>No products found. Try a different search.</p>}

        <div className="product-list">
          {products.map((productPair, index) => (
            <ProductCard key={index} productPair={productPair} />
          ))}
        </div>
      </main>
    </div>
  );
}

export default App;
