import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [categories] = useState(['electronics', 'jewelery', 'men clothing', 'women clothing']);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(false);

  // Handle category selection
  const handleCategoryChange = (e) => {
    setSelectedCategory(e.target.value);
    setProducts([]); // Reset products when category changes
    setSearchTerm(''); // Clear the search input
  };

  // Handle product search
  const handleSearch = async () => {
    if (!selectedCategory || !searchTerm) return;
    setLoading(true);
    try {
      const response = await axios.get(
        `http://localhost:5000/api/products?category=${selectedCategory}&search=${searchTerm}`
      );
      setProducts(response.data);
    } catch (error) {
      console.error('Error fetching products:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      {/* Navbar */}
      <nav className="navbar">
        <div className="logo">ProCompare</div>
        <ul className="nav-links">
          <li><a href="#home">Home</a></li>
          <li><a href="#about">About</a></li>
          <li><a href="#products">Products</a></li>
          <li><a href="#contact">Contact</a></li>
        </ul>
      </nav>

      {/* Main Content */}
      <header className="hero-section">
        <h1>Find the Best Deals, Compare Products</h1>
        <p>Your one-stop solution for smart shopping.</p>
        <div className="search-section">
          {/* Category Selector */}
          <select
            value={selectedCategory}
            onChange={handleCategoryChange}
            className="category-selector"
          >
            <option value="">Select Category</option>
            {categories.map((category, index) => (
              <option key={index} value={category}>
                {category.charAt(0).toUpperCase() + category.slice(1)}
              </option>
            ))}
          </select>

          {/* Search Bar */}
          <div className="search-bar">
            <input
              type="text"
              placeholder="Search for products..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              disabled={!selectedCategory}
            />
            <button onClick={handleSearch} disabled={loading || !selectedCategory}>
              {loading ? 'Loading...' : 'Search'}
            </button>
          </div>
        </div>
      </header>

      {/* Product Comparison */}
      <section id="products" className="product-list">
        {products.length > 0 ? (
          products.map((productPair, index) => (
            <div key={index} className="comparison-card">
              {/* Flipkart Product */}
              <div className="product-card">
                <h3>Flipkart</h3>
                <img src={productPair.flipkart.imageUrl} alt={productPair.flipkart.name} />
                <h3>{productPair.flipkart.name}</h3>
                <p><strong>Price:</strong> {productPair.flipkart.price}</p>
                <p><strong>Rating:</strong> {productPair.flipkart.rating} stars</p>
              </div>

              {/* Amazon Product */}
              <div className="product-card">
                <h3>Amazon</h3>
                <img src={productPair.amazon.imageUrl} alt={productPair.amazon.name} />
                <h3>{productPair.amazon.name}</h3>
                <p><strong>Price:</strong> {productPair.amazon.price}</p>
                <p><strong>Rating:</strong> {productPair.amazon.rating} stars</p>
              </div>
            </div>
          ))
        ) : (
          <p>No products found</p>
        )}
      </section>
    </div>
  );
}

export default App;
