import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [searchTerm, setSearchTerm] = useState('');
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [selectedProducts, setSelectedProducts] = useState([]);

  // Handle the search input
  const handleSearch = async () => {
    if (!searchTerm) return;
    setLoading(true);
    try {
      const response = await axios.get(`http://localhost:5000/api/products?search=${searchTerm}`);
      setProducts(response.data);
    } catch (error) {
      console.error('Error fetching data:', error);
    } finally {
      setLoading(false);
    }
  };

  // Add or remove products for comparison
  const toggleProductSelection = (product) => {
    setSelectedProducts((prevSelected) =>
      prevSelected.includes(product)
        ? prevSelected.filter((p) => p !== product)
        : [...prevSelected, product]
    );
  };

  return (
    <div className="App">
      <h1>Product Comparison</h1>
      <input
        type="text"
        placeholder="Search for products..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />
      <button onClick={handleSearch} disabled={loading}>
        {loading ? 'Loading...' : 'Search'}
      </button>

      {selectedProducts.length > 0 && (
        <div className="comparison-table">
          <h2>Comparison Table</h2>
          <table>
            <thead>
              <tr>
                <th>Feature</th>
                {selectedProducts.map((product, index) => (
                  <th key={index}>{product.name}</th>
                ))}
              </tr>
            </thead>
            <tbody>
              <tr>
                <td>Price</td>
                {selectedProducts.map((product, index) => (
                  <td key={index}>${product.price}</td>
                ))}
              </tr>
              <tr>
                <td>Rating</td>
                {selectedProducts.map((product, index) => (
                  <td key={index}>{product.rating} stars</td>
                ))}
              </tr>
              {/* Add more rows for other features */}
            </tbody>
          </table>
        </div>
      )}

      <div className="product-list">
        {products.length > 0 ? (
          products.map((product, index) => (
            <div key={index} className="product-card">
              <img src={product.imageUrl} alt={product.name} />
              <h3>{product.name}</h3>
              <p>{product.description}</p>
              <p><strong>Price:</strong> ${product.price}</p>
              <p><strong>Rating:</strong> {product.rating} stars</p>
              <a href={product.link} target="_blank" rel="noopener noreferrer">
                View on {product.platform}
              </a>
              <button onClick={() => toggleProductSelection(product)}>
                {selectedProducts.includes(product) ? 'Remove from Comparison' : 'Compare'}
              </button>
            </div>
          ))
        ) : (
          <p>No products found</p>
        )}
      </div>
    </div>
  );
}

export default App;
