// ComparisonHistory.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './comparisonHistory.css'; // Import CSS for styling

function ComparisonHistory({ username }) {
  const [history, setHistory] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchHistory = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/comparisons?username=${username}`);
        setHistory(response.data);
      } catch (err) {
        console.error('Error fetching history:', err.message);
        setError('Failed to fetch comparison history.');
      }
    };

    fetchHistory();
  }, [username]);

  return (
    <div className="comparison-history">
      <div className="profile-header">
        <img
          src="/path/to/profile-logo.png" // Replace with actual path to profile logo
          alt="User Profile"
          className="profile-logo"
        />
        <h1>{username}'s Profile</h1>
      </div>

      <h2>Comparison History</h2>
      {error && <p className="error-message">{error}</p>}

      <ul>
        {history.length > 0 ? (
          history.map((entry, index) => (
            <li key={index} className="history-item">
              <p><strong>Category:</strong> {entry.category}</p>
              <p><strong>Search Term:</strong> {entry.searchTerm}</p>
              <p><strong>Platforms:</strong> {entry.platforms.join(', ')}</p>
              <p><strong>Date:</strong> {new Date(entry.timestamp).toLocaleString()}</p>
            </li>
          ))
        ) : (
          <p>No comparison history found.</p>
        )}
      </ul>
    </div>
  );
}

export default ComparisonHistory;
