import React, { useState, useEffect } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState("");
  const [searchTerm, setSearchTerm] = useState("");
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  // Fetch categories
  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/categories");
        setCategories(response.data);
        setSelectedCategory(response.data[0]); // Default to first category
      } catch (err) {
        console.error("Error fetching categories:", err);
        setError("Failed to fetch categories.");
      }
    };
    fetchCategories();
  }, []);

  const handleSearch = async () => {
    if (!selectedCategory || !searchTerm) return;

    setLoading(true);
    setError("");

    try {
      const response = await axios.get(
        `http://localhost:5000/api/products?category=${selectedCategory}&search=${searchTerm}`
      );
      setProducts(response.data);
    } catch (err) {
      console.error("Error fetching products:", err);
      setError("Failed to fetch products. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <header>
        <h1>Product Comparison</h1>
        <div>
          <select
            value={selectedCategory}
            onChange={(e) => setSelectedCategory(e.target.value)}
          >
            {categories.map((category, index) => (
              <option key={index} value={category}>
                {category.charAt(0).toUpperCase() + category.slice(1)}
              </option>
            ))}
          </select>
          <input
            type="text"
            placeholder="Search for a product"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <button onClick={handleSearch}>Search</button>
        </div>
      </header>

      <main>
        {error && <p className="error">{error}</p>}
        {loading && <p>Loading...</p>}
        {!loading &&
          !error &&
          products.map((product, index) => (
            <div key={index} className="product-comparison">
              <h2>{product.name}</h2>
              <div>
                <h3>Flipkart</h3>
                <p>Price: {product.flipkart.price}</p>
                <p>Rating: {product.flipkart.rating}</p>
                <p>{product.flipkart.description}</p>
                <img src={product.flipkart.imageUrl} alt={product.name} />
              </div>
              {product.amazon ? (
                <div>
                  <h3>Amazon</h3>
                  <p>Price: {product.amazon.price}</p>
                  <p>Rating: {product.amazon.rating}</p>
                  <p>{product.amazon.description}</p>
                  <img src={product.amazon.imageUrl} alt={product.name} />
                </div>
              ) : (
                <p>Not available on Amazon</p>
              )}
            </div>
          ))}
      </main>
    </div>
  );
}

export default App;
