import React from 'react';

function ProductCard({ productPair }) {
  return (
    <div className="product-cards-container">
      {/* Flipkart Product Card */}
      <div className="product-card">
        <img src={productPair.flipkart.imageUrl} alt={productPair.flipkart.name} />
        <div className="product-info">
          <h4>{productPair.flipkart.name}</h4>
          <p className="price">₹{productPair.flipkart.price}</p>
          <p className="rating">Rating: {productPair.flipkart.rating} stars</p>
          <p className="description">{productPair.flipkart.description}</p>
          <p className="order">{productPair.flipkart.order}</p>
        </div>
      </div>

      {/* Amazon Product Card */}
      <div className="product-card">
        <img src={productPair.amazon.imageUrl} alt={productPair.amazon.name} />
        <div className="product-info">
          <h4>{productPair.amazon.name}</h4>
          <p className="price">₹{productPair.amazon.price}</p>
          <p className="rating">Rating: {productPair.amazon.rating} stars</p>
          <p className="description">{productPair.amazon.description}</p>
          <p className="order">{productPair.amazon.order}</p>
        </div>
      </div>
    </div>
  );
}

export default ProductCard;
