import React, { useRef, useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import './App.css';
import Login from './components/Login';
import Register from './components/Register';
import About from './components/About';
import Contact from './components/Contact';
import ProductCard from './components/ProductCard';
import axios from 'axios';
import Developers from './components/Developers';
import ComparisonHistory from './components/comparisonHistory';
import useScrollAnimation from './components/useScrollAnimation';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [categories] = useState(['electronics', 'jewelry', 'men_clothing', 'women_clothing']);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedPlatforms, setSelectedPlatforms] = useState([]);
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [username, setUsername] = useState('');



  const handleLogin = (username) => {
    setUsername(username);
    setIsLoggedIn(true);
  };

  const handleRegister = (username) => {
    setUsername(username);
    alert(`Welcome, ${username}!`);
    setIsLoggedIn(true);
  };

  const handleCategoryChange = (e) => {
    setSelectedCategory(e.target.value);
    setProducts([]);
    setSearchTerm('');
    setError('');
  };

  const handleSearch = async () => {
    if (!selectedCategory || !searchTerm || selectedPlatforms.length === 0) {
      setError('Please select a category, enter a search term, and choose at least one platform.');
      return;
    }

    setLoading(true); 
    setError('');

    try {
      const response = await axios.get(
        `http://localhost:5000/api/products?category=${selectedCategory}&search=${searchTerm}&platforms=${selectedPlatforms.join(',')}&username=${username}`
      );
      setProducts(response.data);
    } catch (err) {
      console.error('Error fetching products:', err);
      setError('Failed to fetch products. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handlePlatformChange = (platform) => {
    setSelectedPlatforms((prev) =>
      prev.includes(platform) ? prev.filter((p) => p !== platform) : [...prev, platform]
    );
  };

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen((prevState) => !prevState);
  };

  const Navbar = () => (
    <nav className={`navbar ${isMobileMenuOpen ? 'active' : ''}`}>
      <div className="logo">ProCompare</div>
      <ul className="nav-links">
        <li><Link to="/">Home</Link></li>
        <li><Link to="/about">About</Link></li>
        <li><Link to="/contact">Contact</Link></li>
        <li><Link to="/developers">Developers</Link></li>
        <li><Link to="/profile">Profile</Link></li>
      </ul>
      <div className="hamburger" onClick={toggleMobileMenu}>
        <div></div>
        <div></div>
        <div></div>
      </div>
    </nav>
  );

  const ProductsSection = () => {
    const [currentIndex, setCurrentIndex] = useState(0);
    const cardsRef = useRef(null);

    const scrollLeft = () => {
      if (currentIndex > 0) {
        setCurrentIndex(currentIndex - 1);
        // Adjust the translateX value to scroll left
        cardsRef.current.style.transform = `translateX(-${(currentIndex - 1) * 320}px)`; // 320px = card width + gap
      }
    };

    const scrollRight = () => {
      if (currentIndex < Math.floor(products.length / 3)) { // Max index based on 3 cards shown
        setCurrentIndex(currentIndex + 1);
        // Adjust the translateX value to scroll right
        cardsRef.current.style.transform = `translateX(-${(currentIndex + 1) * 320}px)`; // 320px = card width + gap
      }
    };

    return (
      <section id="products" className="product-list">
        <button className="arrow-button left" onClick={scrollLeft}>&#60;</button>
        <div className="product-cards" ref={cardsRef}>
          {products.map((productPair, index) => (
            <div key={index}>
              <ProductCard productPair={productPair} />
            </div>
          ))}
        </div>
        <button className="arrow-button right" onClick={scrollRight}>&#62;</button>
      </section>
    );
  };





  const CategorySelector = () => (
    <div className="category-selector-container">
      {categories.map((category, index) => (
        <div
          key={index}
          className={`category-card ${selectedCategory === category ? 'selected' : ''}`}
          onClick={() => handleCategoryChange({ target: { value: category } })}
        >
          <h4>{category.charAt(0).toUpperCase() + category.slice(1)}</h4>
        </div>
      ))}
    </div>
  );

  const PlatformSelector = () => {
    const platformLogos = {
      flipkart: 'https://upload.wikimedia.org/wikipedia/commons/7/72/Flipkart_logo.png',
      amazon: 'https://upload.wikimedia.org/wikipedia/commons/a/a9/Amazon_logo.svg',
      myntra: 'https://upload.wikimedia.org/wikipedia/commons/8/89/Myntra_logo.png',
      meesho: 'https://upload.wikimedia.org/wikipedia/commons/2/26/Meesho_Logo.png',
    };

    return (
      <div className="platform-selector-container">
        <h3>Select Platforms:</h3>
        <div className="platform-selector">
          {Object.keys(platformLogos).map((platform) => (
            <div
              key={platform}
              className={`platform-chip ${selectedPlatforms.includes(platform) ? 'selected' : ''}`}
              onClick={() => handlePlatformChange(platform)}
            >
              <img src={platformLogos[platform]} alt={`${platform} logo`} />
              <span>{platform.charAt(0).toUpperCase() + platform.slice(1)}</span>
            </div>
          ))}
        </div>
      </div>
    );
  };




  const BenefitsSection = () => {
    useScrollAnimation(); 

    return (
      <section className="benefits-section">
        <h2>Why Choose ProCompare ?</h2>
        <div className="benefits-container">
          <div className="benefit-item">
            <img src="i3.webp" alt="Comparison Feature 1" />
            <p>Compare products across multiple platforms effortlessly.</p>
          </div>
          <div className="benefit-item">
            <img src="i2.jpeg" alt="Comparison Feature 2" />
            <p>Save time and money by finding the best deals instantly.</p>
          </div>
          <div className="benefit-item">
            <img src="i4.jpeg" alt="Comparison Feature 3" />
            <p>Access detailed insights and product reviews.</p>
          </div>
          <div className="benefit-item">
            <img src="i1.jpg" alt="Comparison Feature 4" />
            <p>Track price trends and stay ahead of the market.</p>
          </div>
        </div>
      </section>
    );
  };


  return (
    <Router>
      <div className="App">
        {isLoggedIn ? (
          <>
            <Navbar />
            <Routes>
              <Route
                path="/"
                element={
                  <>
                    <header id="home" className="home-layout">
                      <div className="left-side">
                        <h1>Welcome to ProCompare</h1>
                        <button className="compare-button" onClick={() => document.getElementById("products").scrollIntoView({ behavior: "smooth" })}>
                          Compare
                        </button>
                      </div>
                      <div className="right-side">
                        <img src="finalmanleft.png" alt="Home Illustration" className="home-image" />
                      </div>
                    </header>
                    <BenefitsSection />
                    <div className="search-section">
                      <h1>Compare</h1>
                      <div className="search-bar">
  <CategorySelector />
  <input
    type="text"
    placeholder="Search products..."
    value={searchTerm}
    onChange={(e) => setSearchTerm(e.target.value)}
    className="search-input"
  />
  <button onClick={handleSearch} disabled={loading || !selectedCategory} className="search-button">
    {loading ? 'Loading...' : 'Search'}
  </button>
  {error && <p className="error-message">{error}</p>} {/* Display error message */}
</div>

  <PlatformSelector />
</div>

                    <ProductsSection />
                  </>
                }
              />
              <Route path="/about" element={<About />} />
              <Route path="/contact" element={<Contact />} />
              <Route path="/developers" element={<Developers />} />
              <Route path="/profile" element={<ComparisonHistory username={username} />} />
            </Routes>
          </>
        ) : (
          <Routes>
            <Route path="/" element={<Login onLogin={handleLogin} />} />
            <Route path="/register" element={<Register onRegister={handleRegister} />} />
          </Routes>
        )}
      </div>
    </Router>
  );
}

export default App;
