import React, { useState } from 'react';
import './App.css';
import ProductCard from './components/ProductCard';
import { amazonData, flipkartData } from '';

function App() {
  const [categories] = useState(['electronics', 'jewelery', 'men clothing', 'women clothing']);
  const [selectedCategory, setSelectedCategory] = useState(categories[0]);
  const [searchTerm, setSearchTerm] = useState('');
  const [filteredProducts, setFilteredProducts] = useState([]);

  const handleSearch = () => {
    const amazonFiltered = amazonData.filter(
      (product) =>
        product.category.toLowerCase() === selectedCategory.toLowerCase() &&
        product.name.toLowerCase().includes(searchTerm.toLowerCase())
    );

    const flipkartFiltered = flipkartData.filter(
      (product) =>
        product.category.toLowerCase() === selectedCategory.toLowerCase() &&
        product.name.toLowerCase().includes(searchTerm.toLowerCase())
    );

    const groupedProducts = amazonFiltered.map((amazonProduct) => {
      const flipkartProduct = flipkartFiltered.find(
        (product) => product.name.toLowerCase() === amazonProduct.name.toLowerCase()
      );
      return { amazon: amazonProduct, flipkart: flipkartProduct };
    });

    setFilteredProducts(groupedProducts);
  };

  return (
    <div className="App">
      <header>
        <h1>Find the Best Deals, Compare Products</h1>
        <div>
          <select
            value={selectedCategory}
            onChange={(e) => setSelectedCategory(e.target.value)}
          >
            {categories.map((category, index) => (
              <option key={index} value={category}>
                {category}
              </option>
            ))}
          </select>
          <input
            type="text"
            placeholder="Search for a product"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <button onClick={handleSearch}>Search</button>
        </div>
      </header>
      <div className="product-comparison">
        <h2>Product Comparison</h2>
        {filteredProducts.length > 0 ? (
          filteredProducts.map((pair, index) => (
            <div key={index} className="comparison-pair">
              <ProductCard platform="Amazon" product={pair.amazon} />
              <ProductCard platform="Flipkart" product={pair.flipkart} />
            </div>
          ))
        ) : (
          <p>No products found. Try a different search term or category.</p>
        )}
      </div>
    </div>
  );
}

export default App;
