import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import './App.css';
import Login from './components/Login';
import Register from './components/Register';
import ProductCard from './components/ProductCard';
import axios from 'axios';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [categories] = useState([
    'electronics',
    'jewelery',
    'men clothing',
    'women clothing',
  ]);
  const [selectedCategory, setSelectedCategory] = useState(categories[0]);
  const [searchTerm, setSearchTerm] = useState('');
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleCategoryChange = (e) => {
    setSelectedCategory(e.target.value);
    setProducts([]);
    setSearchTerm('');
    setError('');
  };

  const handleSearch = async () => {
    if (!selectedCategory || !searchTerm) return;

    setLoading(true);
    setError('');

    try {
      const response = await axios.get(
        `http://localhost:5000/api/products?category=${selectedCategory}&search=${searchTerm}`
      );
      setProducts(response.data);
    } catch (err) {
      console.error('Error fetching products:', err);
      setError('Failed to fetch products. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleLogin = (username) => {
    setIsLoggedIn(true);
  };

  const handleRegister = (username) => {
    setIsLoggedIn(true);
  };

  const Navbar = () => (
    <nav className="navbar">
      <div className="logo">ProCompare</div>
      <ul className="nav-links">
        <li><a href="#home">Home</a></li>
        <li><a href="#about">About</a></li>
        <li><a href="#contact">Contact</a></li>
      </ul>
    </nav>
  );

  const AboutSection = () => (
    <section id="about" className="section">
      <h2>About</h2>
      <p>Welcome to ProCompare! The ultimate platform for comparing products across different online stores.</p>
    </section>
  );

  const ContactSection = () => (
    <section id="contact" className="section">
      <h2>Contact</h2>
      <p>Feel free to reach out to us at contact@procompare.com</p>
    </section>
  );

  const ProductsSection = () => (
    <section id="products" className="product-list">
      {error && <p className="error-message">{error}</p>}
      {loading && <p>Loading products...</p>}
      {!loading && !error && products.length > 0 ? (
        <div className="product-cards">
          {products.map((productPair, index) => (
            <ProductCard key={index} productPair={productPair} />
          ))}
        </div>
      ) : (
        !loading && !error && <p>No products found. Try a different search term or category.</p>
      )}
    </section>
  );

  return (
    <Router>
      <div className="App">
        {isLoggedIn ? (
          <>
            <Navbar />
            <header id="home">
              <h1>Find the Best Deals, Compare Products</h1>
              <div className="search-section">
                <div className="search-bar">
                  <select
                    value={selectedCategory}
                    onChange={handleCategoryChange}
                    className="category-selector"
                  >
                    {categories.map((category, index) => (
                      <option key={index} value={category}>
                        {category.charAt(0).toUpperCase() + category.slice(1)}
                      </option>
                    ))}
                  </select>
                  <input
                    type="text"
                    placeholder="Search for products..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="search-input"
                  />
                  <button onClick={handleSearch} disabled={loading || !selectedCategory}>
                    {loading ? 'Loading...' : 'Search'}
                  </button>
                </div>
              </div>
            </header>
            <ProductsSection />
            <AboutSection />
            <ContactSection />
          </>
        ) : (
          <Routes>
            <Route path="/login" element={<Login onLogin={handleLogin} />} />
            <Route path="/register" element={<Register onRegister={handleRegister} />} />
            <Route path="/" element={<Login onLogin={handleLogin} />} />
          </Routes>
        )}
      </div>
    </Router>
  );
}

export default App;
