import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [categories, setCategories] = useState(['Electronics', 'Fashion', 'Home Appliances', 'Books']); // Mock categories
  const [selectedCategory, setSelectedCategory] = useState('');
  const [products, setProducts] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading] = useState(false);

  // Fetch products by category
  const fetchProductsByCategory = async (category) => {
    setSelectedCategory(category);
    setSearchTerm(''); // Clear search input
    setLoading(true);
    try {
      const response = await axios.get(`http://localhost:5000/api/products?category=${category}`);
      setProducts(response.data);
    } catch (error) {
      console.error('Error fetching category products:', error);
    } finally {
      setLoading(false);
    }
  };

  // Handle the search input
  const handleSearch = async () => {
    if (!searchTerm) return;
    setSelectedCategory(''); // Clear selected category
    setLoading(true);
    try {
      const response = await axios.get(`http://localhost:5000/api/products?search=${searchTerm}`);
      setProducts(response.data);
    } catch (error) {
      console.error('Error fetching searched products:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      {/* Navbar */}
      <nav className="navbar">
        <div className="logo">ProCompare</div>
        <ul className="nav-links">
          {categories.map((category, index) => (
            <li key={index} onClick={() => fetchProductsByCategory(category)}>
              {category}
            </li>
          ))}
        </ul>
      </nav>

      {/* Hero Section */}
      <header className="hero-section">
        <h1>Find the Best Deals, Compare Products</h1>
        <p>Your one-stop solution for smart shopping.</p>
        <div className="search-bar">
          <input
            type="text"
            placeholder="Search for products..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <button onClick={handleSearch} disabled={loading}>
            {loading ? 'Loading...' : 'Search'}
          </button>
        </div>
      </header>

      {/* Product List */}
      <section id="products" className="product-list">
        <h2>{selectedCategory ? `${selectedCategory} Products` : searchTerm ? `Results for "${searchTerm}"` : 'Products'}</h2>
        {loading ? (
          <p>Loading...</p>
        ) : products.length > 0 ? (
          products.map((product, index) => (
            <div key={index} className="product-card">
              <img src={product.imageUrl} alt={product.name} />
              <h3>{product.name}</h3>
              <p>{product.description}</p>
              <p><strong>Price:</strong> ${product.price}</p>
              <p><strong>Rating:</strong> {product.rating || 'N/A'} stars</p>
              <a href={product.link} target="_blank" rel="noopener noreferrer">
                View on {product.platform}
              </a>
            </div>
          ))
        ) : (
          <p>No products found</p>
        )}
      </section>
    </div>
  );
}

export default App;
