import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [category, setCategory] = useState('');
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(false);

  const categories = ['electronics', 'jewelery', "men's clothing", "women's clothing"]; // Available categories

  // Fetch products based on category
  const handleFetchProducts = async () => {
    if (!category) return alert('Please select a category');
    setLoading(true);
    try {
      const response = await axios.get(`http://localhost:5000/api/products?category=${category}`);
      setProducts(response.data);
    } catch (error) {
      console.error('Error fetching products:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      {/* Navbar */}
      <nav className="navbar">
        <div className="logo">ProCompare</div>
      </nav>

      {/* Category Selection */}
      <section className="category-section">
        <h1>Select a Category to Compare</h1>
        <select value={category} onChange={(e) => setCategory(e.target.value)}>
          <option value="">Select Category</option>
          {categories.map((cat, index) => (
            <option key={index} value={cat}>
              {cat}
            </option>
          ))}
        </select>
        <button onClick={handleFetchProducts} disabled={loading}>
          {loading ? 'Loading...' : 'Compare'}
        </button>
      </section>

      {/* Product Comparison */}
      <section className="product-list">
        {products.length > 0 ? (
          products.map((product, index) => (
            <div key={index} className="product-card">
              <img src={product.image} alt={product.title} />
              <h3>{product.title}</h3>
              <p><strong>Price:</strong> ${product.price}</p>
              <p><strong>Rating:</strong> {product.rating?.rate || 'N/A'} stars</p>
              <p><strong>Platform:</strong> {product.platform}</p>
            </div>
          ))
        ) : (
          <p>No products found</p>
        )}
      </section>
    </div>
  );
}

export default App;
