// /client/src/components/ProductCard.js
import React from 'react';

function ProductCard({ productPair }) {
  return (
    <div className="product-card">
      <div className="flip-card">
        <div className="flip-card-inner">
          {/* Flipkart Side */}
          <div className="flip-card-front">
            <h3>Flipkart</h3>
            <img src={productPair.flipkart.imageUrl} alt={productPair.flipkart.name} />
            <div className="product-info">
              <h4>{productPair.flipkart.name}</h4>
              <p className="price">₹{productPair.flipkart.price}</p>
              <p className="rating">Rating: {productPair.flipkart.rating}</p>
            </div>
          </div>

          {/* Amazon Side */}
          <div className="flip-card-back">
            <h3>Amazon</h3>
            <img src={productPair.amazon.imageUrl} alt={productPair.amazon.name} />
            <div className="product-info">
              <h4>{productPair.amazon.name}</h4>
              <p className="price">₹{productPair.amazon.price}</p>
              <p className="rating">Rating: {productPair.amazon.rating}</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default ProductCard;
