// /client/src/components/ProductCard.js
import React from 'react';

function ProductCard({ productPair }) {
  return (
    <div className="product-card">
      <div className="product">
        <h3>Flipkart</h3>
        <img src={productPair.flipkart.imageUrl} alt={productPair.flipkart.name} />
        <p>{productPair.flipkart.name}</p>
        <p>Price: ₹{productPair.flipkart.price}</p>
        <p>Rating: {productPair.flipkart.rating}</p>
      </div>
      <div className="product">
        <h3>Amazon</h3>
        <img src={productPair.amazon.imageUrl} alt={productPair.amazon.name} />
        <p>{productPair.amazon.name}</p>
        <p>Price: ₹{productPair.amazon.price}</p>
        <p>Rating: {productPair.amazon.rating}</p>
      </div>
    </div>
  );
}

export default ProductCard;
