import React, { useEffect, useState } from 'react';
import axios from 'axios';

function ComparisonHistory({ username }) {
  const [comparisonHistory, setComparisonHistory] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchComparisonHistory = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/comparison-history?username=${username}`);
        setComparisonHistory(response.data);
      } catch (err) {
        console.error('Error fetching comparison history:', err);
        setError('Failed to fetch comparison history. Please try again.');
      }
    };

    if (username) {
      fetchComparisonHistory();
    }
  }, [username]);

  return (
    <div>
      <h2>{username}'s Profile</h2>
      <h3>Comparison History</h3>
      {error && <p>{error}</p>}
      {comparisonHistory.length > 0 ? (
        <ul>
          {comparisonHistory.map((item, index) => (
            <li key={index}>{item.productName}</li>
          ))}
        </ul>
      ) : (
        <p>No comparison history available.</p>
      )}
    </div>
  );
}

export default ComparisonHistory;
